import React, { Component } from 'react';
import './App.css';
import Movie from './Movie';

class App extends Component {

  state ={}

  _renderMovie = () => {
    const movie = this.state.movies.map((movie, index) => {
      return <Movie key={index} title={movie.title} poster={movie.medium_cover_image} />
    })
    return movie
  }

  _getMovies =() =>{
    fetch('https://yts.lt/api/v2/list_movies.json?quality=3D?')
    .then(response => response.json())
    .then(json =>{
      this.setState({
        movies: json.data.movies
      })
    })
    .catch(err => console.log(err))

  }

  _callApi = () =>{

  }

  render() {
    return (
      <div className="app">
        {this.state.movies ? this._renderMovie() : 'Loading'}
      </div>
    );
  }

  componentDidMount() {
    this._getMovies();
  }
}

export default App;
